public class MeasureMain {
    public static void main(String[] args) {
        System.out.println("Object size is: " + ObjectSizeFetcher.getObjectSize(new Object()));
        System.out.println("Long size is: " + ObjectSizeFetcher.getObjectSize(new Long(1L)));
        System.out.println("1024 BigInteger size is: " + ObjectSizeFetcher.getObjectSize(new BigInteger("", 10)));
        System.out.println("2048 BigInteger size is: " + ObjectSizeFetcher.getObjectSize(new BigInteger("", 10)));
    }

}
